import React from 'react';

function ChatList({ chats, setSelectedChat }) {
    return (
        <div className="chat-list">
            <h2>Chats:</h2>
            <ul>
                {chats.map((chat) => (
                    <li key={chat._id} onClick={() => setSelectedChat(chat)}>
                        <strong>{chat.chat_name}</strong>: {chat.lastMessage ? chat.lastMessage.content : "No messages"}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ChatList;
