import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import Auth from './Auth';

function App() {
  const [token, setToken] = useState(null);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    // Перевірка наявності токена в URL після перенаправлення
    const queryParams = new URLSearchParams(window.location.search);
    const tokenFromUrl = queryParams.get('token');

    if (tokenFromUrl) {
      // Збереження токена в localStorage
      localStorage.setItem('token', tokenFromUrl);
      setToken(tokenFromUrl);
      // Очистка URL
      window.history.replaceState({}, document.title, "/");
    } else {
      // Отримання токена з localStorage
      const savedToken = localStorage.getItem('token');
      setToken(savedToken);
    }

    if (token) {
      // Підключення до Socket.IO з токеном
      const socketIo = io('http://localhost:3000', {
        query: { token }
      });
      setSocket(socketIo);

      // Очистка при розмонтажі компоненти
      return () => {
        socketIo.disconnect();
      };
    }
  }, [token]);

  const handleFetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/protected', {
        headers: {
          'Authorization': `Bearer ${token}` // Додаємо токен до заголовків
        }
      });
      alert(`Response: ${JSON.stringify(response.data)}`);
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to fetch data.');
    }
  };

  const handleSendMessage = () => {
    if (socket) {
      socket.emit('sendMessage', { room: 'myRoom', message: 'Hello, room!' });
    }
  };

  useEffect(() => {
    if (socket) {
      socket.on('message', (message) => {
        alert(`New message: ${JSON.stringify(message)}`);
      });
    }
  }, [socket]);

  return (
      <div className="App">
        {!token ? (
            <Auth />
        ) : (
            <div>
              <h1>Welcome you are in app</h1>
              <button onClick={handleFetchData}>Fetch Data</button>
              <button onClick={handleSendMessage}>Send Message</button>
            </div>
        )}
      </div>
  );
}

export default App;
