import React from 'react';

function Auth({ setToken }) {
    // Логіка авторизації
    const handleLogin = () => {
        // Тут має бути логіка авторизації через Google
        // Наприклад, використання бібліотеки Google API для авторизації

        // Після отримання токена з Google, збережіть його в локальному сховищі та оновіть стан
        const token = 'your-token-from-google';
        localStorage.setItem('token', token);
        setToken(token);
    };

    return (
        <div className="auth-container">
            <button onClick={handleLogin}>Login with Google</button>
        </div>
    );
}

export default Auth;
