import React, {useState, useEffect, useRef} from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import {jwtDecode} from 'jwt-decode';
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Auth from './components/Auth';
import ChatList from './components/ChatList';
import MessageList from './components/MessageList';
import './App.css';
import InputField from "./components/InputField";
import UniversalChatModal from "./components/UniversalChatModal"; // Імпорт стилів
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faPaperPlane} from '@fortawesome/free-solid-svg-icons';
import SearchBar from "./components/search-bar/SearchBar";
import UniversalButton from "./components/universal-button/UniversalButton";

function App() {
    const [token, setToken] = useState(null);
    const [socket, setSocket] = useState(null);
    const [messages, setMessages] = useState([]);
    const [chats, setChats] = useState([]);
    const [selectedChat, setSelectedChat] = useState(null);
    const selectedChatRef = useRef(null);
    const [messageToSend, setMessageToSend] = useState('');
    const [showModal, setShowModal] = useState(false);
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [initialFirstName, setInitialFirstName] = useState('');
    const [initialLastName, setInitialLastName] = useState('');

    useEffect(() => {
        selectedChatRef.current = selectedChat;
    }, [selectedChat]);

    const handleEditChat = async (chat) => {
        setInitialFirstName(chat.first_name);
        setInitialLastName(chat.last_name);
        setIsEditing(true);
        setFirstName(chat.first_name);
        setLastName(chat.last_name);
        setSelectedChat(chat.chat_name);
        setShowModal(true);
    };
    const handleDeleteChat = async (chatId) => {
        try {
            await axios.delete(`http://localhost:3000/chat/delete-chat/${chatId}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            setChats((prevChats) => prevChats.filter(chat => chat.chat_name !== chatId));
            if (selectedChat === chatId) {
                setSelectedChat(null);
                setMessages([]);
            }
        } catch (error) {
            console.error('Error deleting chat:', error);
        }
    };

    // Декодування токена для отримання userId
    const getUserIdFromToken = (token) => {
        try {
            const decodedToken = jwtDecode(token);
            return decodedToken.userId; // Або інший ключ, де зберігається ID користувача
        } catch (error) {
            console.error('Error decoding token:', error);
            return null;
        }
    };

    const userId = token ? getUserIdFromToken(token) : null;

    useEffect(() => {
        const queryParams = new URLSearchParams(window.location.search);
        const tokenFromUrl = queryParams.get('token');

        if (tokenFromUrl) {
            localStorage.setItem('token', tokenFromUrl);
            setToken(tokenFromUrl);
            window.history.replaceState({}, document.title, "/");
        } else {
            const savedToken = localStorage.getItem('token');
            setToken(savedToken);
        }

        if (token) {
            const socketIo = io('http://localhost:3000', {
                query: {token}
            });
            setSocket(socketIo);

            socketIo.on('connect', () => {
                console.log("Socket connected");
                fetchChats(socketIo);
            });

            socketIo.on('message', (message) => {
                console.log('New message received:', message);
                console.log(selectedChatRef.current === message.room)
                console.log("room" + message.room)
                console.log(selectedChatRef.current)
                console.log("MessUserId" + message.user);
                console.log(userId);

                if (selectedChatRef.current === message.room) {
                    setMessages((prevMessages) => [...prevMessages, message]);
                }

                if (message.user !== userId) {
                    toast.info(`New message from ${message.chatName}`);
                }
            });

            return () => {
                socketIo.disconnect();
            };
        }
    }, [token]);


    const handleUpdateChat = async () => {
        if (!firstName || !lastName) {
            alert('Both fields are required');
            return;
        }

        try {
            await axios.put(`http://localhost:3000/chat/update-chat/${selectedChat}`, {
                first_name: firstName,
                last_name: lastName
            }, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            setChats((prevChats) => prevChats.map(chat =>
                chat.chat_name === selectedChat
                    ? {...chat, first_name: firstName, last_name: lastName}
                    : chat
            ));

            setSelectedChat(selectedChat);
            setShowModal(false);
        } catch (error) {
            console.error('Error updating chat:', error);
        }
    };

    const fetchChats = async (socketIo) => {
        try {
            const response = await axios.get('http://localhost:3000/chat/all-user-chats', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            setChats(response.data);
            console.log(response.data);

            response.data.forEach(chat => {
                socketIo.emit('joinRoom', chat.chat_name);
            });
        } catch (error) {
            console.error('Error fetching chats:', error);
        }
    };

    const handleSendMessage = () => {
        if (socket && selectedChat && messageToSend) {
            console.log("Вибраний чат при сенд месседж" + selectedChat);
            socket.emit('sendMessage', {room: selectedChat, message: messageToSend});
            setMessageToSend('');
        }
    };

    const handleChatSelect = async (chatId) => {
        setSelectedChat(chatId);


        selectedChatRef.current = chatId; // Переконайтесь, що ви оновлюєте реф також
        console.log("Вибраний чат" + selectedChatRef.current)
        setMessages([]); // Очищення повідомлень при виборі нового чату

        try {
            const response = await axios.get(`http://localhost:3000/chat/get-chat/${chatId}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            setMessages(response.data.messages);
        } catch (error) {
            console.error('Error fetching chat messages:', error);
        }
    };


    const filteredChats = chats.filter(chat =>
        `${chat.first_name} ${chat.last_name}`.toLowerCase().includes(searchTerm.toLowerCase())
    );
    const handleCreateChat = async () => {
        if (!firstName || !lastName) {
            alert('Both fields are required');
            return;
        }

        try {
            const response = await axios.post('http://localhost:3000/chat/create-chat', {
                firstName,
                lastName
            }, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            const newChatName = response.data;
            console.log('newChatName')
            console.log(newChatName)
            setChats((prevChats) => [...prevChats, newChatName]);
            socket.emit('joinRoom', newChatName.chat_name);
            setSelectedChat(newChatName);
            setShowModal(false);
        } catch (error) {
            console.error('Error creating chat:', error);
        }
    };


    const openCreateChatModal = () => {
        setFirstName('');
        setLastName('');
        setIsEditing(false);
        setShowModal(true);
    };

    const openEditChatModal = (chat) => {
        setInitialFirstName(chat.first_name);
        setInitialLastName(chat.last_name);
        setIsEditing(true);
        setShowModal(true);
    };

    const handleSubmit = isEditing ? handleUpdateChat : handleCreateChat;


    return (
        <div className="app-container">
            <ToastContainer/>
            {!token ? (
                <Auth/>
            ) : (
                <div className="chat-app">
                    <div className="chat-list">
                        <div className={"header-chat-list"}>
                            <div>
                            <div className="user-icon">
                                <i className="fa-solid fa-user"></i>
                                <i className="fa-solid fa-check"></i>
                            </div>
                            <Auth/>
                            </div>
                            <div>
                            <h1>Chats</h1>
                            </div>
                            <SearchBar searchTerm={searchTerm}
                                       setSearchTerm={setSearchTerm}/> {/* Використання SearchBar */}
                            <UniversalButton onClick={openCreateChatModal} label="Create Chat"/>
                        </div>
                        <ChatList
                            chats={filteredChats}
                            selectedChat={selectedChat}
                            onSelectChat={handleChatSelect}
                            onEditChat={handleEditChat}
                            onDeleteChat={handleDeleteChat}
                        />
                    </div>
                    <div className="message-area">
                        <div className="message-info">
                            {selectedChat ? (
                                <div>
                                    <h3>Chat Info</h3>
                                    <p>{`First Name: ${selectedChat.first_name}`}</p>
                                    <p>{`Last Name: ${selectedChat.last_name}`}</p>
                                    <UniversalButton onClick={() => openEditChatModal(selectedChat)} label="Edit Chat"/>
                                </div>
                            ) : (
                                <p>Select a chat to see info</p>
                            )}
                        </div>
                        <div className="message-list">
                            <MessageList messages={messages} userId={userId}/>
                        </div>
                        <div className="message-input">
                            <input
                                type="text"
                                value={messageToSend}
                                onChange={(e) => setMessageToSend(e.target.value)}
                                placeholder="Type your message..."
                            />
                            <button onClick={handleSendMessage} className="send-button">
                                <FontAwesomeIcon icon={faPaperPlane}/>
                            </button>
                        </div>


                    </div>
                    {showModal && (
                        <UniversalChatModal
                            isEditing={isEditing}
                            initialFirstName={initialFirstName}
                            initialLastName={initialLastName}
                            firstName={firstName}
                            lastName={lastName}
                            setFirstName={setFirstName}
                            setLastName={setLastName}
                            handleSubmit={handleSubmit}
                            closeModal={() => setShowModal(false)}
                        />
                    )}
                </div>
            )}
        </div>
    );
}

export default App;
