import React from 'react';
import '../ChatList.css';
import UniversalButton from "./universal-button/UniversalButton";

const ChatList = ({ chats, selectedChat, onSelectChat, onEditChat, onDeleteChat }) => {
    return (
        <div className="chat-list-container">
            <ul>
                {chats.map((chat) => (
                    <li
                        key={chat._id}
                        className={`chat-item ${selectedChat === chat.chat_name ? 'selected' : ''}`}
                        onClick={() => onSelectChat(chat.chat_name)}
                    >
                        <strong>{chat.first_name} {chat.last_name}</strong>
                        <div className="chat-actions">
                            <UniversalButton onClick={(e) => { e.stopPropagation(); onEditChat(chat); }} label="Edit"/>
                            <UniversalButton onClick={(e) => { e.stopPropagation(); onDeleteChat(chat.chat_name); }} label="Delete"/>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ChatList;
