import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import Auth from './components/Auth';
import './App.css';
function App() {
  const [token, setToken] = useState(null);
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [chats, setChats] = useState([]);

  useEffect(() => {
    const queryParams = new URLSearchParams(window.location.search);
    const tokenFromUrl = queryParams.get('token');

    if (tokenFromUrl) {
      localStorage.setItem('token', tokenFromUrl);
      setToken(tokenFromUrl);
      window.history.replaceState({}, document.title, "/");
    } else {
      const savedToken = localStorage.getItem('token');
      setToken(savedToken);
    }

    if (token) {
      const socketIo = io('http://localhost:3000', {
        query: { token }
      });
      setSocket(socketIo);

      socketIo.on('connect', () => {
        console.log("Socket connected");

        // Запит чатів після підключення
        fetchChats(socketIo);
      });

      socketIo.on('message', (message) => {
        console.log('New message received:', message);
        setMessages((prevMessages) => [...prevMessages, message]);
      });

      return () => {
        socketIo.disconnect();
      };
    }
  }, [token]);

  const fetchChats = async (socketIo) => {
    try {
      const response = await axios.get('http://localhost:3000/chat/all-user-chats', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setChats(response.data);
      alert(response.data)
      console.log(response.data);

      response.data.forEach(chat => {
        socketIo.emit('joinRoom', chat.chat_name);
      });
    } catch (error) {
      console.error('Error fetching chats:', error);
    }
  };

  const handleFetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/protected', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert(`Response: ${JSON.stringify(response.data)}`);
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to fetch data.');
    }
  };

  const handleSendMessageToRoom = (roomName) => {
    if (socket) {
      socket.emit('sendMessage', { room: roomName, message: `Hello, ${roomName}!` });
    }
  };

  return (
      <div className="App">
        {!token ? (
            <Auth />
        ) : (
            <div>
              <h1>Welcome you are in app</h1>
              <button onClick={handleFetchData}>Fetch Data</button>
              <div>
                <h2>Chats:</h2>
                <ul>
                  {chats.map((chat) => (
                      <li key={chat._id}>
                        <strong>{chat.chat_name}</strong>: {chat.lastMessage ? chat.lastMessage.content : "No messages"}
                        <button onClick={() => handleSendMessageToRoom(chat.chat_name)}>Send Message to this Room</button>
                      </li>
                  ))}
                </ul>
              </div>
              <div>
                <h2>Messages:</h2>
                <ul>
                  {messages.map((msg, index) => (
                      <li key={index}>{`${msg.user}: ${msg.message}`}</li>
                  ))}
                </ul>
              </div>
            </div>
        )}
      </div>
  );
}

export default App;
