import React from 'react';

function Auth({ setToken }) {
    const handleLogin = () => {
        // Логіка авторизації через Google
        // Наприклад, використання бібліотеки Google API для авторизації

        // Імітація отримання токена з Google
        const token = 'your-token-from-google'; // Цей токен потрібно замінити на реальний токен після авторизації
        if (token) {
            localStorage.setItem('token', token);
            setToken(token);
            window.location.href = `/?token=${token}`;
        }
    };

    return (
        <div className="auth-container">
            <button onClick={handleLogin}>Login with Google</button>
        </div>
    );
}

export default Auth;
