import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import useToken from './hooks/useToken';
import Auth from './components/Auth';
import ChatList from './components/ChatList';
import ChatWindow from './components/ChatWindow';
import './App.css';

function App() {
  const { token, setToken } = useToken();
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);

  useEffect(() => {
    if (token) {
      const socketIo = io('http://localhost:3000', {
        query: { token }
      });
      setSocket(socketIo);

      socketIo.on('connect', () => {
        console.log("Socket connected");

        // Запит чатів після підключення
        fetchChats(socketIo);
      });

      socketIo.on('message', (message) => {
        console.log('New message received:', message);
        setMessages((prevMessages) => [...prevMessages, message]);
      });

      return () => {
        socketIo.disconnect();
      };
    }
  }, [token]);

  const fetchChats = async (socketIo) => {
    // Виклик API для отримання чатів
  };

  return (
      <div className="App">
        {!token ? (
            <Auth setToken={setToken} />
        ) : (
            <div className="chat-container">
              <ChatList chats={chats} setSelectedChat={setSelectedChat} />
              <ChatWindow selectedChat={selectedChat} messages={messages} socket={socket} />
            </div>
        )}
      </div>
  );
}

export default App;
