import React, { useEffect } from 'react';
import InputField from './InputField';

function UniversalChatModal({
                                isEditing,
                                initialFirstName,
                                initialLastName,
                                firstName,
                                lastName,
                                setFirstName,
                                setLastName,
                                handleSubmit,
                                closeModal
                            }) {
    useEffect(() => {
        // Заповнення полів початковими даними при відкритті модального вікна для редагування
        if (isEditing) {
            setFirstName(initialFirstName);
            setLastName(initialLastName);
        } else {
            setFirstName('');
            setLastName('');
        }
    }, [isEditing, initialFirstName, initialLastName, setFirstName, setLastName]);

    return (
        <div className="modal">
            <div className="modal-content">
                <h2>{isEditing ? 'Edit Chat' : 'Create New Chat'}</h2>
                <InputField
                    label="First Name"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                />
                <InputField
                    label="Last Name"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                />
                <button onClick={handleSubmit}>{isEditing ? 'Save Changes' : 'Create'}</button>
                <button onClick={closeModal}>Cancel</button>
            </div>
        </div>
    );
}

export default UniversalChatModal;
